(372, 1, '2021-01-04 05:59:40', '2021-01-04 05:59:40', '<!-- wp:html -->\n[row]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_sidebar]\n\n\n[/col]\n[col span=\"9\" span__sm=\"12\"]\n\n[row_inner]\n\n[col_inner span__sm=\"12\" margin=\"10px 0px 0px 0px\"]\n\n<p class=\"page-content\" style=\"font-size:26px;font-weight:700;\">\nChuyên thiết kế và In phông họp báo, in phông họp lớp tại Hà Nội \n</p>\n\n[/col_inner]\n\n[/row_inner]\n[ux_image id=\"807\" width=\"60\"]\n\n<p class=\"page-content\">\nIn phông họp báo “Cuộc thi hoa hậu Việt Nam”\n</p>\n[ux_image id=\"808\" width=\"60\"]\n\n[ux_image id=\"809\" width=\"60\"]\n\n[ux_image id=\"810\" width=\"60\"]\n\n<p class=\"page-content\" style=\"font-size:26px;font-weight:700;\">\nIn phông họp báo năm du lịch quốc gia\n</p>\n[ux_image id=\"811\" width=\"60\"]\n\n<p class=\"page-content\">\nHọp báo là một trong nhứng hoạt động diễn ra thường xuyên, nhằm mục tiêu loan báo các thông tin cần thiết từ một hoặc một số chủ thể (cá nhân hoặc tổ chức) với các phương tiện truyền thông đại chúng để thông tin đó đến được với công chúng.\n</p>\n\n[/col]\n\n[/row]\n\n<!-- /wp:html -->', 'In phông họp lớp', '', 'publish', 'closed', 'closed', '', 'in-phong-hop-lop', '', '', '2021-01-06 10:12:21', '2021-01-06 10:12:21', '', 0, 'http://localhost:8080/doan/?page_id=372', 0, 'page', '', 0),
